import React from 'react';
import { Box, Heading, Text } from '@chakra-ui/react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

const SecurityScore = () => {
  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Security Score',
        data: [65, 70, 75, 72, 80, 85],
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Security Score Trend',
      },
    },
  };

  return (
    <Box p={4} borderRadius="lg" boxShadow="base" bg="white" mt={6}>
      <Heading size="md" mb={4}>Security Score History</Heading>
      <Line data={data} options={options} />
      <Text mt={4} color="gray.600">
        Your security score has improved by 20 points in the last 6 months.
      </Text>
    </Box>
  );
};

export default SecurityScore;
