import React, { useState } from 'react';
import {
  Box,
  Button,
  VStack,
  HStack,
  Heading,
  Text,
  Container,
  Icon,
  Flex,
  Badge,
  Grid,
  Input,
  FormControl,
  FormLabel,
  FormErrorMessage,
  InputGroup,
  InputLeftElement,
  useToast,
  StatHelpText,
  StatArrow,
  useToken,
} from '@chakra-ui/react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { 
  FiShield, 
  FiLock, 
  FiActivity, 
  FiPieChart, 
  FiUsers, 
  FiAlertTriangle,
  FiTrendingUp,
  FiDatabase,
  FiServer,
  FiMail
} from 'react-icons/fi';
import { IconType } from 'react-icons';

interface StatCardProps {
  icon: IconType;
  label: string;
  value: string;
  change?: number;
  color: string;
}

interface ActivityItem {
  icon: IconType;
  text: string;
  time: string;
}

const StatCard = ({ icon, label, value, change, color }: StatCardProps) => (
  <Box
    p={6}
    borderRadius="2xl"
    bg="rgba(26, 28, 42, 0.5)"
    borderWidth="1px"
    borderColor="rgba(255, 255, 255, 0.1)"
    backdropFilter="blur(10px)"
    transition="all 0.3s"
    _hover={{
      transform: "translateY(-2px)",
      boxShadow: `0 8px 32px ${color}30`,
    }}
  >
    <Flex align="center" justify="space-between">
      <VStack align="start" spacing={1}>
        <Text color="whiteAlpha.600" fontSize="sm">{label}</Text>
        <Heading size="lg" color="white">{value}</Heading>
        {change && (
          <StatHelpText color={change >= 0 ? "green.400" : "red.400"} mb={0}>
            <StatArrow type={change >= 0 ? "increase" : "decrease"} />
            {Math.abs(change)}%
          </StatHelpText>
        )}
      </VStack>
      <Box
        p={3}
        borderRadius="xl"
        bgGradient={`linear-gradient(135deg, ${color}30, ${color}10)`}
      >
        <Icon as={icon} w={6} h={6} color={color} />
      </Box>
    </Flex>
  </Box>
);

const Login = () => {
  const { login, error, isLoading, clearError} = useAuth();
  const navigate = useNavigate();
  const [purple, cyan] = useToken('colors', ['purple.400', 'cyan.400']);
  const toast = useToast();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    if (isSubmitting) return;

    try {
      setIsSubmitting(true);
      await login(email, password);
      toast({
        title: 'Welcome back!',
        description: 'Successfully logged in. Redirecting to dashboard...',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
      // Add a slight delay to show the success message
      setTimeout(() => {
        navigate('/');
      }, 1000);
    } catch (err) {
      toast({
        title: 'Login failed',
        description: error || 'Please check your credentials and try again.',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Box
      minH="100vh"
      bgGradient="linear-gradient(135deg, #13151F 0%, #1E2137 100%)"
      position="relative"
      overflow="hidden"
      py={8}
    >
      {/* Gradient Orbs */}
      <Box
        position="absolute"
        top="-20%"
        right="-10%"
        width="600px"
        height="600px"
        background="radial-gradient(circle, rgba(149, 128, 255, 0.2) 0%, transparent 70%)"
        filter="blur(60px)"
        animation="pulse 8s infinite"
      />
      <Box
        position="absolute"
        bottom="-30%"
        left="-15%"
        width="800px"
        height="800px"
        background="radial-gradient(circle, rgba(128, 255, 234, 0.15) 0%, transparent 70%)"
        filter="blur(60px)"
        animation="pulse 10s infinite"
      />
      
      <Container maxW="container.xl" position="relative">
        <VStack spacing={12} align="stretch">
          {/* Header Section */}
          <Flex justify="space-between" align="center">
            <HStack spacing={4}>
              <Box
                p={2}
                borderRadius="xl"
                bgGradient="linear-gradient(135deg, rgba(149, 128, 255, 0.2), rgba(128, 255, 234, 0.2))"
              >
                <Icon as={FiShield} w={8} h={8} color="#9580FF" />
              </Box>
              <VStack align="start" spacing={1}>
                <Heading 
                  size="lg" 
                  bgGradient="linear-gradient(135deg, #9580FF, #80FFEA)"
                  bgClip="text"
                >
                  ThreatIntel AI
                </Heading>
                <Text color="whiteAlpha.600">Enterprise Security Platform</Text>
              </VStack>
            </HStack>
          </Flex>

          {/* Main Content */}
          <Grid templateColumns={{ base: "1fr", lg: "1fr 1.5fr" }} gap={8}>
            {/* Left Side - Login */}
            <VStack spacing={6} align="stretch">
              <Box
                p={8}
                borderRadius="2xl"
                bg="rgba(26, 28, 42, 0.5)"
                borderWidth="1px"
                borderColor="rgba(255, 255, 255, 0.1)"
                backdropFilter="blur(10px)"
              >
                <form onSubmit={handleLogin}>
                  <VStack spacing={6} align="stretch">
                    <VStack align="start" spacing={2}>
                      <Heading 
                        size="xl" 
                        bgGradient="linear-gradient(135deg, #9580FF, #80FFEA)"
                        bgClip="text"
                      >
                        Welcome Back
                      </Heading>
                      <Text color="whiteAlpha.800">
                        Access your security command center
                      </Text>
                      <Text color="whiteAlpha.600" fontSize="sm">
                        Demo credentials: admin@threatintel.ai / admin123
                      </Text>
                    </VStack>

                    <VStack spacing={4}>
                      <FormControl isInvalid={!!error}>
                        <InputGroup>
                          <InputLeftElement pointerEvents="none">
                            <Icon as={FiMail} color="whiteAlpha.400" />
                          </InputLeftElement>
                          <Input
                            type="email"
                            placeholder="Email address"
                            value={email}
                            onChange={(e) => {
                              setEmail(e.target.value);
                              clearError();
                            }}
                            bg="rgba(26, 28, 42, 0.5)"
                            border="1px solid"
                            borderColor="whiteAlpha.200"
                            color="white"
                            _placeholder={{ color: 'whiteAlpha.400' }}
                            _hover={{
                              borderColor: 'whiteAlpha.300',
                            }}
                            _focus={{
                              borderColor: '#9580FF',
                              boxShadow: '0 0 0 1px #9580FF',
                            }}
                          />
                        </InputGroup>
                      </FormControl>

                      <FormControl isInvalid={!!error}>
                        <InputGroup>
                          <InputLeftElement pointerEvents="none">
                            <Icon as={FiLock} color="whiteAlpha.400" />
                          </InputLeftElement>
                          <Input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => {
                              setPassword(e.target.value);
                              clearError();
                            }}
                            bg="rgba(26, 28, 42, 0.5)"
                            border="1px solid"
                            borderColor="whiteAlpha.200"
                            color="white"
                            _placeholder={{ color: 'whiteAlpha.400' }}
                            _hover={{
                              borderColor: 'whiteAlpha.300',
                            }}
                            _focus={{
                              borderColor: '#9580FF',
                              boxShadow: '0 0 0 1px #9580FF',
                            }}
                          />
                        </InputGroup>
                        <FormErrorMessage>
                          {error}
                        </FormErrorMessage>
                      </FormControl>
                    </VStack>

                    <Button
                      type="submit"
                      size="lg"
                      width="full"
                      height="60px"
                      bgGradient="linear-gradient(135deg, #9580FF, #80FFEA)"
                      color="white"
                      fontSize="lg"
                      isLoading={isSubmitting}
                      loadingText="Authenticating..."
                      _hover={{
                        bgGradient: "linear-gradient(135deg, #8070EF, #70EFDA)",
                        transform: "translateY(-2px)",
                        boxShadow: "0 8px 32px rgba(149, 128, 255, 0.4)",
                      }}
                      leftIcon={<Icon as={FiLock} />}
                    >
                      Enter Secure Dashboard
                    </Button>
                  </VStack>
                </form>
              </Box>

              {/* Security Stats */}
              <Grid templateColumns="repeat(2, 1fr)" gap={4}>
                <StatCard
                  icon={FiAlertTriangle}
                  label="Active Threats"
                  value="3"
                  change={-14.5}
                  color="#9580FF"
                />
                <StatCard
                  icon={FiUsers}
                  label="Users Protected"
                  value="1,247"
                  change={8.3}
                  color="#80FFEA"
                />
              </Grid>
            </VStack>

            {/* Right Side - Platform Preview */}
            <Box
              borderRadius="2xl"
              bg="rgba(26, 28, 42, 0.5)"
              borderWidth="1px"
              borderColor="rgba(255, 255, 255, 0.1)"
              backdropFilter="blur(10px)"
              p={8}
              position="relative"
              overflow="hidden"
            >
              <VStack spacing={8} align="stretch">
                <Flex justify="space-between" align="center">
                  <Heading size="md" color="white">Platform Overview</Heading>
                  <Badge
                    colorScheme="purple"
                    px={3}
                    py={1}
                    borderRadius="full"
                    bgGradient="linear-gradient(135deg, rgba(149, 128, 255, 0.2), rgba(128, 255, 234, 0.2))"
                  >
                    Live Data
                  </Badge>
                </Flex>

                <Grid templateColumns="repeat(3, 1fr)" gap={6}>
                  <StatCard
                    icon={FiPieChart}
                    label="Security Score"
                    value="94%"
                    change={5.2}
                    color="#FF80BF"
                  />
                  <StatCard
                    icon={FiServer}
                    label="Systems Monitored"
                    value="1,834"
                    color="#8AFF80"
                  />
                  <StatCard
                    icon={FiDatabase}
                    label="Data Protected"
                    value="12.4 TB"
                    color="#FF9580"
                  />
                </Grid>

                <Box
                  p={6}
                  borderRadius="xl"
                  bg="rgba(26, 28, 42, 0.7)"
                  borderWidth="1px"
                  borderColor="rgba(255, 255, 255, 0.1)"
                >
                  <VStack align="start" spacing={4}>
                    <Text color="white" fontWeight="medium">Recent Activities</Text>
                    {[
                      { icon: FiShield, text: "Threat detection updated", time: "2m ago" },
                      { icon: FiActivity, text: "System scan completed", time: "15m ago" },
                      { icon: FiTrendingUp, text: "Security score improved", time: "1h ago" },
                    ].map((activity, index) => (
                      <HStack key={index} spacing={3} color="whiteAlpha.800">
                        <Icon as={activity.icon} color="#9580FF" />
                        <Text flex="1">{activity.text}</Text>
                        <Text fontSize="sm" color="whiteAlpha.600">{activity.time}</Text>
                      </HStack>
                    ))}
                  </VStack>
                </Box>
              </VStack>
            </Box>
          </Grid>
        </VStack>
      </Container>
    </Box>
  );
};

export default Login;
