import React from 'react';
import { Box, Heading, List, ListItem, Text, Badge, Flex } from '@chakra-ui/react';

const RecentAlerts = () => {
  const alerts = [
    {
      id: 1,
      type: 'high',
      message: 'Suspicious login attempt detected',
      time: '2 minutes ago'
    },
    {
      id: 2,
      type: 'medium',
      message: 'Unusual file access pattern',
      time: '15 minutes ago'
    },
    {
      id: 3,
      type: 'low',
      message: 'New device connected to network',
      time: '1 hour ago'
    }
  ];

  const getBadgeColor = (type: string) => {
    switch (type) {
      case 'high':
        return 'red';
      case 'medium':
        return 'orange';
      case 'low':
        return 'yellow';
      default:
        return 'gray';
    }
  };

  return (
    <Box p={4} borderRadius="lg" boxShadow="base" bg="white">
      <Heading size="md" mb={4}>Recent Alerts</Heading>
      <List spacing={3}>
        {alerts.map(alert => (
          <ListItem key={alert.id} p={3} borderRadius="md" bg="gray.50">
            <Flex justify="space-between" align="center">
              <Box>
                <Badge colorScheme={getBadgeColor(alert.type)} mb={2}>
                  {alert.type.toUpperCase()}
                </Badge>
                <Text>{alert.message}</Text>
              </Box>
              <Text fontSize="sm" color="gray.500">{alert.time}</Text>
            </Flex>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default RecentAlerts;
