import React from 'react';
import { Box, Heading, Text, Flex, Progress, Icon, Grid, Badge, VStack } from '@chakra-ui/react';
import { Line } from 'react-chartjs-2';
import { FiTrendingUp, FiShield, FiCheck } from 'react-icons/fi';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const SecurityScore = () => {
  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Security Score',
        data: [65, 70, 75, 72, 80, 85],
        fill: true,
        backgroundColor: 'rgba(129, 140, 248, 0.1)',
        borderColor: '#818cf8',
        tension: 0.4,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
    },
  };

  return (
    <Box p={4} borderRadius="lg" boxShadow="base" bg="white" mt={6}>
      <Heading size="md" mb={4}>Security Score History</Heading>
      <Line key="security-score-chart" data={data} options={options} />
      <Text mt={4} color="gray.600">
        Your security score has improved by 20 points in the last 6 months.
      </Text>
    </Box>
  );
};

export default SecurityScore;
